import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Navigation from './Navigation';
import Footer from './Footer';

function ServicesPage() {
  const location = useLocation();

  useEffect(() => {
    // Scroll to section if hash is present, but skip machine-control as it's at the top
    if (location.hash && location.hash !== '#machine-control') {
      const element = document.querySelector(location.hash);
      if (element) {
        setTimeout(() => {
          element.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }, 100);
      }
    }
  }, [location]);
  return (
    <div className="min-h-screen bg-gray-900">
      <Navigation />

      <section
        className="relative bg-gradient-to-b from-gray-800 to-gray-900 text-white"
        style={{
          backgroundImage: 'url(/services-image.png)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          minHeight: '80vh'
        }}
      >
        <div className="absolute inset-0 bg-gray-900 bg-opacity-70"></div>
        <div className="relative max-w-4xl mx-auto text-center px-4">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">What We Do</h1>
        </div>
      </section>

      {/* Service Cards Section - positioned at bottom of viewport */}
      <section className="py-16 px-4 max-w-6xl mx-auto bg-gray-900">
        <div className="grid md:grid-cols-3 gap-8">
          {/* Machine Control Modeling Card */}
          <div className="bg-gray-800 rounded-lg p-8 shadow-xl border border-gray-700 hover:border-ab-blue transition-colors cursor-pointer" onClick={() => window.location.href='/services/machine-control'}>
            <div className="text-4xl mb-4">üìê</div>
            <h3 className="text-2xl font-bold text-white mb-4">3D Machine Control Modeling</h3>
            <p className="text-gray-400 mb-6">Transform your plans into precision machine control models. Every surface, every grade, every detail‚Äîbuilt with systematic accuracy and delivered when you need it.</p>
            <div className="text-ab-blue font-semibold">Learn More ‚Üí</div>
          </div>

          {/* Takeoffs Card */}
          <div className="bg-gray-800 rounded-lg p-8 shadow-xl border border-gray-700 hover:border-ab-blue transition-colors cursor-pointer" onClick={() => window.location.href='/services/takeoffs'}>
            <div className="text-4xl mb-4">üìä</div>
            <h3 className="text-2xl font-bold text-white mb-4">Takeoffs & Earthwork Calculations</h3>
            <p className="text-gray-400 mb-6">Get accurate volume calculations and cut/fill analysis you can stake your bid on. Our detailed takeoffs give you the numbers that matter.</p>
            <div className="text-ab-blue font-semibold">Learn More ‚Üí</div>
          </div>

          {/* Remote Support Card */}
          <div className="bg-gray-800 rounded-lg p-8 shadow-xl border border-gray-700 hover:border-ab-blue transition-colors cursor-pointer" onClick={() => window.location.href='/services/remote-support'}>
            <div className="text-4xl mb-4">üõ†Ô∏è</div>
            <h3 className="text-2xl font-bold text-white mb-4">Remote Support</h3>
            <p className="text-gray-400 mb-6">Never lose productivity to calibration issues. Our real-time remote support gets your operators back to work fast, with expert guidance when you need it.</p>
            <div className="text-ab-blue font-semibold">Learn More ‚Üí</div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

export default ServicesPage;
